    private OrtSession.Result LoadORT() throws OrtException, IOException {
        ai.onnxruntime.OrtEnvironment env = OrtEnvironment.getEnvironment();
        OrtSession.SessionOptions session_options = new OrtSession.SessionOptions();
        session_options.addConfigEntry("session.load_model_format", "ORT");
        InputStream ins = getResources().openRawResource(
                getResources().getIdentifier("9_13_opsest12_all",
                        "raw", getPackageName()));
        byte[] model = readFully(ins);
        OrtSession session = env.createSession(model,session_options);
        byte[] imgData = convertBitmapToByteBuffer(mBitmap);
        OnnxTensor tensorfromBuffer  = OnnxTensor.createTensor(env,imgData);
        Map<String, OnnxTensor> inputs = new HashMap<>();
        assert tensorfromBuffer != null;
        inputs.put("img", tensorfromBuffer);
        OrtSession.Result output = session.run(inputs);
        return output;


    private byte[] convertBitmapToByteBuffer(Bitmap bitmap) {

        ByteArrayOutputStream stream = new ByteArrayOutputStream();
        bitmap.compress(Bitmap.CompressFormat.PNG, 100, stream);
        byte[] byteArray = stream.toByteArray();
        bitmap.recycle();
        return byteArray;
    }



    public static byte[] readFully(InputStream input) throws IOException {
        byte[] buffer = new byte[8192];
        int bytesRead;
        ByteArrayOutputStream output = new ByteArrayOutputStream();
        while ((bytesRead = input.read(buffer)) != -1)
        {
            output.write(buffer, 0, bytesRead);
        }
        return output.toByteArray();
    }
```


